    public function getFormattedPhoneAttribute(): ?string
    {
        if (! $this->phone) {
            return null;
        }

        return preg_replace('/(\d{3})(\d{3})(\d{4})/', '($1) $2-$3', $this->phone);
    }

    public function getAverageOrderValueAttribute(): float
    {
        $orderCount = $this->order_count;

        if ($orderCount === 0) {
            return 0;
        }

        return $this->total_spent / $orderCount;
    }

    public function getAgeAttribute(): ?int
    {
        if (! $this->birth_date) {
            return null;
        }

        return $this->birth_date->age;
    }

    public function redeemLoyaltyPoints(int $points): bool
    {
        if ($this->loyalty_points < $points) {
            return false;
        }

        $this->loyalty_points -= $points;
        $this->save();

        return true;
    }

    public function getStockStatusAttribute(): string
    {
        if ($this->quantity === 0) {
            return 'out_of_stock';
        }

        if ($this->isLowStock()) {
            return 'low_stock';
        }

        if ($this->isOverstock()) {
            return 'overstock';
        }

        return 'in_stock';
    }

public function getStockStatusColorAttribute(): string
{
    return match ($this->stock_status) {
        'out_of_stock' => 'red',
        'low_stock' => 'yellow',
        'overstock' => 'blue',
        'in_stock' => 'green',
        default => 'gray',
    };
}

    public function deductStock(int $quantity): bool
    {
        if (! $this->canFulfillOrder($quantity)) {
            return false;
        }

        $this->quantity -= $quantity;
        $this->save();

        return true;
    }

public function getStatusColorAttribute(): string
{
    return match ($this->status) {
        'pending' => 'gray',
        'confirmed' => 'blue',
        'preparing' => 'yellow',
        'ready' => 'green',
        'completed' => 'emerald',
        'cancelled' => 'red',
        default => 'gray',
    };
}

public function getOrderTypeLabelAttribute(): string
{
    return match ($this->order_type) {
        'dine_in' => 'Dine In',
        'takeaway' => 'Takeaway',
        'delivery' => 'Delivery',
        default => ucfirst($this->order_type),
    };
}

public function canBeModified(): bool
{
    return in_array($this->status, ['pending', 'confirmed']);
}

public function canBeCancelled(): bool
{
    return in_array($this->status, ['pending', 'confirmed', 'preparing']);
}

    public function getPreparationTimeAttribute(): ?int
    {
        if (! $this->estimated_ready_time || ! $this->order_date) {
            return null;
        }

        return $this->order_date->diffInMinutes($this->estimated_ready_time);
    }

    public function getActualPreparationTimeAttribute(): ?int
    {
        if (! $this->completed_at || ! $this->order_date) {
            return null;
        }

        return $this->order_date->diffInMinutes($this->completed_at);
    }

    public function updateStatus(string $status): void
    {
        $this->status = $status;

        if ($status === 'completed') {
            $this->completed_at = now();
        }

        $this->save();
    }

public function scopeThisMonth($query)
{
    return $query->whereMonth('order_date', now()->month)
        ->whereYear('order_date', now()->year);
}

public function getStatusColorAttribute(): string
{
    return match ($this->status) {
        'pending' => 'gray',
        'confirmed' => 'blue',
        'preparing' => 'yellow',
        'ready' => 'green',
        'completed' => 'emerald',
        'cancelled' => 'red',
        default => 'gray',
    };
}

    public function getCustomizationSummaryAttribute(): string
    {
        if (! $this->customizations || empty($this->customizations)) {
            return 'None';
        }

        $summary = [];

        foreach ($this->customizations as $key => $value) {
            if (is_array($value)) {
                $summary[] = ucfirst($key).': '.implode(', ', $value);
            } else {
                $summary[] = ucfirst($key).': '.$value;
            }
        }

        return implode(', ', $summary);
    }

public function hasCustomizations(): bool
{
    return ! empty($this->customizations);
}

public function canBeModified(): bool
{
    return in_array($this->status, ['pending', 'confirmed']);
}

public function canBeCancelled(): bool
{
    return in_array($this->status, ['pending', 'confirmed', 'preparing']);
}

    public function updateQuantity(int $quantity): void
    {
        if ($quantity <= 0) {
            return;
        }

        $this->quantity = $quantity;
        $this->calculateSubtotal();
    }

public function getStatusColorAttribute(): string
{
